INSERT INTO `met_job` (`id`, `position`, `count`, `place`, `deal`, `addtime`, `useful_life`, `content`, `access`, `no_order`, `wap_ok`, `top_ok`, `email`, `filename`, `lang`, `displaytype`) VALUES
(3, 'Marketing Director', 1, 'changsha', '10K+', '2018-01-13', 30, '<p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;"><strong>Job content:</strong></span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">1, responsible for the market development of the whole series of Canon products in the 3-5 level city.</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">2. Collect and analyze the market information and plan the local market strategy.</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">3, coordinate the various product departments within the company to support the overall market sales of the 3-5 level cities.</span></p><p><br/></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;"><strong>Requirement：</strong><br/></span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">1, college or above, over 30 years of age, more than 5 years of large company market or sales experience;</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">2, the copywriting ability is strong, the market acuity is high, and the IT/OA/ digital industry is familiar.</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">3. It has the ability to plan and co-ordinate the product and market, and can adapt to the appropriate business trip.</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">4, fluent in Japanese, English is especially good.</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">5. Have the ability to coordinate and communicate.</span></p><p><br/></p>', 0, 0, 0, 0, '123321123@111.com', '', 'en', 1),
(4, 'Environmental Promotion Commissioner', 1, 'changsha', '8k+', '2018-01-13', 30, '<p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;"><strong>Post requirements:</strong></span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">Bachelor degree or above, environmental or legal related major</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">More than 5 years of electronic or mechanical product environment promotion experience, top five hundred enterprise work experience is preferred</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">Understand RoHS, WEEE related knowledge, and be familiar with environmental code standards such as environmental signs and energy efficiency signs</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">Fluency in Japanese and English and English communication and writing skills</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">Good interpersonal communication ability and team cooperative learning ability</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;"><strong>Job content：</strong></span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">Follow up and know the trend and requirements of the environmental laws and regulations related to the enterprise, and carry out the response</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">Communicate and communicate with headquarters, regional headquarters and various departments of the company in Asia Law and regulation business, and coordinate related business development</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">Internal and external environmental related laws and regulations education activities</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">Actively carrying out environmental protection and promoting activities inside and outside the company</span></p><p><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;">Assisting the boss to finish the work in the Department</span></p><p><br/></p>', 0, 0, 0, 0, '112315@111.com', '', 'en', 1);
